import React, { PureComponent } from 'react'

export default class About extends PureComponent{
    constructor(props){
        super(props)
        this.state = {}
    }

    currencyFormat = (num) => {
        return '$' + Number.parseFloat(num).toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')              
    }

    generateRandomTradePrice = (capital) => {
		return capital < 3000 ? this.generateNumLessThanOne() 
			: capital < 8000 ? this.generateNum(3)
			: capital < 15000 ? this.generateNum(5)
			: capital < 25000 ? this.generateNum(7)
			: this.generateNum(8)
	}

	generateNum = (num) => {
		let precision = 100; // 2 decimals
		let tradePrice = Math.floor(Math.random() * (num * precision - 1 * precision) + 1 * precision) / (1 * precision);
        return tradePrice * 100; //account for 100 shares at that price
	}

	generateNumLessThanOne = () => {  
		debugger
		var rand = Math.random()*(1-.20) + .20;
		var power = Math.pow(10, 2);
		let result = Math.floor(rand*power) / power;
		return result;
	}

	calculateConsYouCanAfford = (capital, optionPrice) => {
		debugger
		let howManyConsYouCanAfford = capital / optionPrice;
		return Math.floor(howManyConsYouCanAfford);
	}

    calculateEarnings = () => {
        let capital = 500;
        let yearlyObj = [];

        // start with $500, you play $50 cons and cash out at 20% every day, assuming you ride the rest free.
        // will only account for 4 days a week you win a trade

        //year loop
        for(let i = 1; i <= 50; i++){
			debugger
            let weekObj = [];
            let weeklyProfits = 0;

            for(let j = 1; j < 6; i++){
                //week is over
                if(i === 5){
                    weeklyProfits = this.currencyFormat(weeklyProfits);
                    let weekCompleteObj = {
                        text: `Weekly profits: `,
                        dailyProfits: null,
                        weeklyProfits: weeklyProfits,
                        totalCapital: capital
                    };
					weekObj.push(weekCompleteObj);
					debugger
                } else {

					//week is beginning
					if(i === 1){
						let titleObj = {
							text: `Week ${i}:`,
							dailyProfits: null,
							weeklyProfits: null,
							totalCapital: null
						};
						weekObj.push(titleObj);
					}

					let optionPrice = this.generateRandomTradePrice(capital);

					debugger

					//get the amount of cons you can buy
					let howManyConsYouCanAfford = this.calculateConsYouCanAfford(capital, optionPrice);
					//caps the cons you play at 50, don't want to play more than that   
					let amountOfCons = howManyConsYouCanAfford > 50 ? 50 : howManyConsYouCanAfford;

					debugger

					//make the trade
					let totalCapitalInTrade = optionPrice * amountOfCons;
					let profitAtTwentyPercent = totalCapitalInTrade * .2;
					let cleanDailyProfits = this.currencyFormat(profitAtTwentyPercent);

					//add to weekly profits
					weeklyProfits += (weeklyProfits + profitAtTwentyPercent);
					let cleanWeeklyProfits = this.currencyFormat(weeklyProfits);

					debugger
					//convert to currency
					let totalCapitalConverted = this.currencyFormat(capital += profitAtTwentyPercent);

					//build obj for array
					let dailyCapitalObj = {
						text: `Day ${j}`,
						dailyProfits: cleanDailyProfits,
						weeklyProfits: cleanWeeklyProfits,
						totalCapital: totalCapitalConverted
					};

					weekObj.push(dailyCapitalObj);
					debugger
				}

            }
			debugger
            yearlyObj.push(weekObj);
        }

        return yearlyObj;
    }

    render(){

        let earnings = this.calculateEarnings();

        return(
            <div>
                {earnings}
            </div>
        )
    }
}

