import React, { PureComponent}  from 'react'

//styled
import Outer from './styled/Outer'

export default class Company extends PureComponent{

    displayList = () => {
        debugger
        let textList = this.props.textList;
        return <ul>
            {textList.map((item) => 
                <li key={item.key}>{item.description}</li>
            )}
        </ul>
    }

    render(){

        const {
            id,
            alt,
            link,
            city,
            state,
            image,
            toDate,
            fromDate
        } = this.props

        let displayList = this.displayList();

        return(
            <Outer>
                <div className="tableRow">
                    <div className="join" id="photo">
                        <a href={link}><img src={image} id={id} alt={alt} /></a>
                        <div>
                            <p>{toDate} - {fromDate}</p>
                            <p>{city}, {state}</p>
                        </div>
                    </div>
                    <div className="join" id="text">
                    {displayList}  
                    </div>
                </div>
            </Outer>
        )
    }
}
