import React, {PureComponent} from 'react'

//styled
import Outer from './styled/Outer'

//components
import BabySitterKata from './components/BabySitter'

export default class Katas extends PureComponent{
    constructor(props){
        super(props)
        this.state = {
            displayBabySitterKata: false
        }
    }

    renderBabySitterKata (value){
        this.setState({ displayBabySitterKata: value})
    }

    render(){
        const {
            displayBabySitterKata
        } = this.state

        return(
            <Outer>
                <div className="buttonRow">
                    <button onClick={this.renderBabySitterKata('displayBabySitterKata ? false : true')}>
                        BabySitterKata
                    </button>
                </div>
                { 
                displayBabySitterKata 
                ?
                    <div className="kataRow">
                        <BabySitterKata />
                    </div> 
                : 
                    null
                }
            </Outer>
        )
    }
}
