import React, { Purecomponent } from 'react'

//styled
import Outer from './styled/Outer'

export default class FancyDropdown extends Purecomponent{

    // renderOptions = () =>{
    //     let options = this.props.options;
    //     return
    //         <div>
    //             <select>
    //             {options.map((option) => {
    //             <option onChage={this.props.onChange} key={option.value} value={option.value}>{option.display}</option>
    //             })}
    //         </select>
    //         </div>
    // }

    render(){

        const {
            options,
            title
        } = this.props

        return(
            <Outer>
                <h3>{title}</h3>
                <select>
                    {options.map((option) => {
                    <option onChage={this.props.onChange} key={option.value} value={option.value}>{option.display}</option>
                    })}
                </select>
            </Outer>
        )
    }
}
